window.addEventListener("load", function() {
    var e = new ScrollMagic.Controller;
    jQuery(".fade-in-bottom").not(".project-title").each(function(i, t) {
        jQuery(t).attr("id", "animate-id-" + i), new ScrollMagic.Scene({
            triggerElement: "#animate-id-" + i,
            triggerHook: .9
        }).on("enter", function() {
            jQuery("#animate-id-" + i).addClass("is-active")
        }).addTo(e)
    }), jQuery(".container-sticky, .container-sidebar").theiaStickySidebar({})
});